pragma solidity 0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

import "../lib/TokenPayments.sol";
import "../modules/sht-module/SHT.sol";
import "../project-funding/ProjectFunding.sol";
import "./Interface.sol";
import "./User.sol";

import { Distribution } from "./distribution/Storage.sol";
import { Epochs } from "../lib/Epochs.sol";
import { HousingStakingToken, NewHousingStakingToken, MIN_EPOCHS_LOCK, MAX_EPOCHS_LOCK, HstAttributes } from "./HST.sol";
import { HousingProject } from "../housing-project/HousingProject.sol";
import { rewardshares } from "../housing-project/RewardSharing.sol";
import { LkSHT } from "../modules/LockedSmartHousingToken.sol";

/// @title SmartHousing
/// @notice SmartHousing enables real estate tokenization for fractional ownership and investment.
/// @dev Main contract for the SmartHousing ecosystem. Manages HousingProjects, users, and staking.
contract SmartHousing is ISmartHousing, Ownable, UserModule, ERC1155Holder {
	using TokenPayments for ERC20TokenPayment;
	using Distribution for Distribution.Storage;
	using Epochs for Epochs.Storage;
	using EnumerableSet for EnumerableSet.AddressSet;
	using TokenPayments for TokenPayment;
	using SafeMath for uint256;

	// Contract addresses and instances
	address public projectFundingAddress;
	address public coinbaseAddress;
	address public shtTokenAddress;
	HousingStakingToken public hst;
	LkSHT public lkSht;

	// Storage for distribution and epochs
	Distribution.Storage public distributionStorage;
	Epochs.Storage public epochs;

	// Permission management
	enum Permissions {
		NONE,
		HOUSING_PROJECT
	}
	mapping(address => Permissions) public permissions;
	EnumerableSet.AddressSet private _projectsToken; // List of project SFT addresses

	/// @notice Constructor to initialize SmartHousing.
	/// @param coinbase Address of the coinbase.
	/// @param projectFunding Address of the ProjectFunding contract.
	constructor(address coinbase, address projectFunding) {
		coinbaseAddress = coinbase;
		projectFundingAddress = projectFunding;
		hst = NewHousingStakingToken.create();
		lkSht = ProjectFunding(projectFundingAddress).lkSht();

		// Initialize epochs and periods (24 hours for mainnet, 30 minutes for testing)
		epochs.initialize(30 minutes);
	}

	/// @notice Register a new user or get the referral ID if already registered.
	/// @param userAddr Address of the user.
	/// @param referrerId Referral ID of the referrer.
	/// @return User ID.
	function createRefIDViaProxy(
		address userAddr,
		uint256 referrerId
	) external onlyProjectFunding returns (uint256) {
		return _createOrGetUserId(userAddr, referrerId);
	}

	/// @notice Setup SHT token and distribute funds.
	/// @param payment Token payment details for SHT setup.
	function setUpSHT(ERC20TokenPayment calldata payment) external {
		require(msg.sender == coinbaseAddress, "Unauthorized");
		require(shtTokenAddress == address(0), "SHT already set");
		shtTokenAddress = address(payment.token);
		require(
			payment.amount == SHT.ECOSYSTEM_DISTRIBUTION_FUNDS,
			"Incorrect SHT amount"
		);
		payment.accept();

		distributionStorage.setTotalFunds(payment.amount);
	}

	/// @notice Add a new project and set its permissions.
	/// @param projectAddress Address of the new project.
	function addProject(address projectAddress) external onlyProjectFunding {
		_setPermissions(projectAddress, Permissions.HOUSING_PROJECT);
		HousingProject project = HousingProject(projectAddress);
		address projectSFTaddress = address(project.projectSFT());
		_projectsToken.add(projectSFTaddress);

		distributionStorage.addProject(
			projectAddress,
			projectSFTaddress,
			project.getMaxSupply()
		);
	}

	/// @notice Add rent to a project and update distribution storage.
	/// @param amount Amount of rent received.
	function addProjectRent(uint256 amount) external onlyHousingProject {
		address projectAddress = msg.sender;
		distributionStorage.addProjectRent(projectAddress, amount);
	}

	/// @notice Stake tokens for rewards.
	/// @param stakingTokens Array of token payments for staking.
	/// @param epochsLock Lock period in epochs.
	/// @param referrerId Referral ID of the referrer.
	function stake(
		TokenPayment[] calldata stakingTokens,
		uint256 epochsLock,
		uint256 referrerId
	) external {
		require(
			epochsLock >= MIN_EPOCHS_LOCK && epochsLock <= MAX_EPOCHS_LOCK,
			"Invalid epochs lock period"
		);
		address caller = msg.sender;

		_createOrGetUserId(caller, referrerId);
		distributionStorage.generateRewards(epochs);

		HstAttributes memory newAttr = _mintHstToken(
			stakingTokens,
			distributionStorage.projectsStakingRewards.checkpoint,
			distributionStorage.shtRewardPerShare,
			epochsLock,
			address(lkSht)
		);

		distributionStorage.enterStaking(newAttr.stakeWeight);
	}

	/// @notice Check if a user can claim rewards.
	/// @param user Address of the user.
	/// @param tokenNonce Nonce of the token.
	/// @return True if the user can claim rewards, otherwise false.
	function userCanClaim(
		address user,
		uint256 tokenNonce
	) public view returns (bool) {
		bool hasSft = hst.hasSFT(user, tokenNonce);
		if (!hasSft) return false;

		bool rewardsCanBeGenerated = distributionStorage
			.lastFundsDispatchTimestamp < block.timestamp;
		if (rewardsCanBeGenerated) return true;

		HstAttributes memory hstAttr = hst.getAttribute(tokenNonce);
		return
			hstAttr.shtRewardPerShare < distributionStorage.shtRewardPerShare;
	}

	/// @notice Claim rewards and update token attributes.
	/// @param hstNonce Nonce of the HST token.
	/// @param referrerId Referral ID of the referrer.
	/// @return newHstNonce New HST nonce.
	function claimRewards(
		uint256 hstNonce,
		uint256 referrerId
	) external returns (uint256 newHstNonce) {
		address caller = msg.sender;
		_createOrGetUserId(caller, referrerId);

		uint256 callerHstBal = hst.balanceOf(caller, hstNonce);
		require(callerHstBal > 0, "No HST token balance at nonce");

		distributionStorage.generateRewards(epochs);
		(uint256 claimedSHT, HstAttributes memory hstAttr) = distributionStorage
			.claimRewards(hst.getAttribute(hstNonce));
		uint256 rentRewards = 0;

		// Claim rent rewards from HousingProjects
		for (uint256 i = 0; i < hstAttr.projectTokens.length; i++) {
			TokenPayment memory projectToken = hstAttr.projectTokens[i];
			require(
				projectToken.token != address(0),
				"Invalid project address"
			);
			address projectAddress = distributionStorage
				.projectSftToProjectAddress[projectToken.token];

			// Call the external contract's claimRentReward function
			(
				,
				rewardshares memory rewardShares,
				uint256 newNonce
			) = HousingProject(projectAddress).claimRentReward(
					projectToken.nonce
				);
			hstAttr.projectTokens[i].nonce = newNonce;

			rentRewards = rentRewards.add(rewardShares.userValue);
		}

		// Update the attributes in the hst token
		newHstNonce = hst.update(
			caller,
			hstNonce,
			callerHstBal,
			abi.encode(hstAttr)
		);

		ERC20Burnable shtToken = ERC20Burnable(shtTokenAddress);
		if (claimedSHT > 0) {
			uint256 referrerValue = claimedSHT.mul(25).div(1000);
			claimedSHT = claimedSHT.sub(referrerValue);
			(, address referrerAddr) = getReferrer(caller);
			if (referrerAddr != address(0)) {
				shtToken.transfer(referrerAddr, referrerValue);
			} else {
				shtToken.burn(referrerValue);
			}
		}

		shtToken.transfer(caller, claimedSHT.add(rentRewards));
	}

	/// @notice Get project distribution details.
	/// @param project Address of the project.
	/// @return Project distribution data.
	function projectDets(
		address project
	) public view returns (Distribution.ProjectDistributionData memory) {
		return distributionStorage.projectDets[project];
	}

	/// @notice Get the list of project tokens.
	/// @return Array of project token addresses.
	function projectsToken() public view returns (address[] memory) {
		return _projectsToken.values();
	}

	// Internal functions

	/// @notice Set permissions for an address.
	/// @param addr Address to set permissions for.
	/// @param perm Permissions to set.
	function _setPermissions(address addr, Permissions perm) internal {
		permissions[addr] = perm;
	}

	function _prepareProjectTokensAndLkShtNonces(
		TokenPayment[] calldata payments,
		address lkShtAddress
	)
		internal
		view
		returns (
			TokenPayment[] memory projectTokens,
			uint256[] memory lkShtNonces
		)
	{
		uint256 projectTokensCount = 0;
		uint256 lkShtNoncesCount = 0;

		for (uint256 i = 0; i < payments.length; i++) {
			TokenPayment memory payment = payments[i];

			if (payment.token == shtTokenAddress) {
				// Do nothing
			} else if (payment.token == lkShtAddress) {
				lkShtNoncesCount++;
			} else if (_projectsToken.contains(payment.token)) {
				projectTokensCount++;
			} else {
				revert("Invalid Sent Token");
			}
		}

		projectTokens = new TokenPayment[](projectTokensCount);
		lkShtNonces = new uint256[](lkShtNoncesCount);
	}

	/// @notice Prepare project tokens and LkSHT for staking.
	/// @param payments Array of tokens to prepare.
	/// @param epochsLock Number of epochs to lock.
	/// @param projectsShareCheckpoint Number of epochs to lock.
	/// @param lkShtAddress Address of the LkSHT token.
	/// @return Attributes of the new HST token.
	function _mintHstToken(
		TokenPayment[] calldata payments,
		uint256 projectsShareCheckpoint,
		uint256 shtRewardPerShare,
		uint256 epochsLock,
		address lkShtAddress
	) internal returns (HstAttributes memory) {
		address caller = msg.sender;

		uint256 projectTokenCount = 0;
		uint256 lkShtNoncesCount = 0;
		uint256 shtAmount = 0;

		(
			TokenPayment[] memory projectTokens,
			uint256[] memory lkShtNonces
		) = _prepareProjectTokensAndLkShtNonces(payments, lkShtAddress);
		require(
			(projectTokens.length + lkShtNonces.length) < 10,
			"Max SFT tokens exceeded"
		);

		for (uint256 i = 0; i < payments.length; i++) {
			TokenPayment memory payment = payments[i];

			if (payment.token == shtTokenAddress) {
				shtAmount = shtAmount.add(payment.amount);
			} else if (payment.token == lkShtAddress) {
				uint256 lkShtBal = lkSht.balanceOf(caller, payment.nonce);
				require(
					lkShtBal == payment.amount,
					"Must send all LkSHT balance"
				);

				shtAmount = shtAmount.add(lkShtBal);
				lkShtNonces[lkShtNoncesCount] = payment.nonce;
				lkShtNoncesCount++;
			} else if (_projectsToken.contains(payment.token)) {
				projectTokens[projectTokenCount] = payment;
				projectTokenCount++;
			} else {
				revert("Invalid Sent Token");
			}

			payment.receiveToken(caller);
		}

		return
			hst.mint(
				projectTokens,
				projectsShareCheckpoint,
				shtRewardPerShare,
				epochsLock,
				shtAmount,
				lkShtNonces,
				caller
			);
	}

	// Modifiers

	/// @notice Modifier to check if caller is authorized to interact with the contract.
	modifier onlyProjectFunding() {
		require(msg.sender == projectFundingAddress, "Not authorized");
		_;
	}

	/// @notice Modifier to check if caller is a housing project.
	modifier onlyHousingProject() {
		require(
			permissions[msg.sender] == Permissions.HOUSING_PROJECT,
			"Not authorized"
		);
		_;
	}
}
","keccak256":"0x804bcf0fa61f24cf97f093795d4f17826b26bf0fdf7badae4a2a3f3f119264a8","license":"MIT"},"contracts/main/User.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "./Interface.sol";

abstract contract UserModule is IUserModule {
	struct ReferralInfo {
		uint256 id;
		address referralAddress;
	}

	struct User {
		uint256 id;
		address addr;
		uint256 referrerId;
		uint256[] referrals;
	}

	uint256 public userCount;
	mapping(address => User) public users;
	mapping(uint256 => address) public userIdToAddress;

	event UserRegistered(
		uint256 userId,
		address userAddress,
		uint256 referrerId
	);
	event ReferralAdded(uint256 referrerId, uint256 referralId);

	/// @notice Register a new user or get the referral ID if already registered.
	/// @param referrerId The ID of the referrer.
	/// @return The ID of the registered user.
	function createRefID(uint256 referrerId) external returns (uint256) {
		address userAddr = msg.sender;
		return _createOrGetUserId(userAddr, referrerId);
	}

	/// @notice Gets the referrer and referrer ID of a user.
	/// @param userAddress The address of the user.
	/// @return referrerId The ID of the referrer, 0 if none.
	/// @return referrerAddress The address of the referrer, address(0) if none.
	function getReferrer(
		address userAddress
	) public view returns (uint256 referrerId, address referrerAddress) {
		User storage user = users[userAddress];
		referrerId = user.referrerId;
		referrerAddress = userIdToAddress[referrerId];
	}

	/// @notice Gets the user ID for a given address.
	/// @param userAddress The address of the user.
	/// @return userId The ID of the user.
	function getUserId(
		address userAddress
	) external view returns (uint256 userId) {
		return users[userAddress].id;
	}

	/// @notice Retrieves the referrals of a user.
	/// @param userAddress The address of the user.
	/// @return referrals An array of `ReferralInfo` structs representing the user's referrals.
	function getReferrals(
		address userAddress
	) external view returns (ReferralInfo[] memory) {
		uint256[] memory referralIds = users[userAddress].referrals;
		ReferralInfo[] memory referrals = new ReferralInfo[](
			referralIds.length
		);

		for (uint256 i = 0; i < referralIds.length; i++) {
			uint256 id = referralIds[i];
			address refAddr = userIdToAddress[id];
			referrals[i] = ReferralInfo({ id: id, referralAddress: refAddr });
		}

		return referrals;
	}

	/// @notice Internal function to create or get the user ID.
	/// @param userAddr The address of the user.
	/// @param referrerId The ID of the referrer.
	/// @return The ID of the user.
	function _createOrGetUserId(
		address userAddr,
		uint256 referrerId
	) internal returns (uint256) {
		User storage user = users[userAddr];

		// If user already exists, return the existing ID
		if (user.id != 0) {
			return user.id;
		}

		// Increment user count and assign new user ID
		userCount++;
		users[userAddr] = User({
			id: userCount,
			addr: userAddr,
			referrerId: referrerId,
			referrals: new uint256[](0)
		});
		userIdToAddress[userCount] = userAddr;

		// Add user to referrer's referrals list, if applicable
		if (
			referrerId != 0 &&
			referrerId != userCount &&
			userIdToAddress[referrerId] != address(0)
		) {
			users[userIdToAddress[referrerId]].referrals.push(userCount);
			emit ReferralAdded(referrerId, userCount);
		}

		emit UserRegistered(userCount, userAddr, referrerId);
		return userCount;
	}
}
","keccak256":"0x03282c2f6767d7cd20c6331e559b41dcfea2b27447bf974b330c7a3fe4c4459e","license":"MIT"},"contracts/main/distribution/Storage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../../lib/Epochs.sol";
import "../../housing-project/HousingProject.sol";
import "../../modules/sht-module/Economics.sol";

import { HstAttributes } from "../HST.sol";

library ProjectStakingRewards {
	/// @notice Structure to track staking rewards.
	/// @param toShare The amount available for distribution.
	/// @param checkpoint The checkpoint to track distributed rewards.
	struct Value {
		uint256 toShare;
		uint256 checkpoint;
	}

	/// @notice Adds a specified value to staking rewards.
	/// @param self The storage struct containing staking rewards.
	/// @param rhs The value to add to staking rewards.
	function add(Value storage self, uint256 rhs) internal {
		self.toShare += rhs;
		self.checkpoint += rhs;
	}

	/// @notice Subtracts a specified value from staking rewards.
	/// @param self The storage struct containing staking rewards.
	/// @param rhs The value to subtract from staking rewards.
	function sub(Value storage self, uint256 rhs) internal {
		self.toShare -= rhs;
	}
}

/// @title Distribution Library for SmartHousing Contract
/// @notice This library manages the distribution of staking rewards and project rent across housing projects within the SmartHousing ecosystem.
/// @dev The library handles the allocation of received rents to specific housing projects, computes staking rewards, and allows users to claim their rewards.
library Distribution {
	using Epochs for Epochs.Storage;
	using Entities for Entities.Value;
	using ProjectStakingRewards for ProjectStakingRewards.Value;

	struct Storage {
		uint256 totalFunds; // Total funds available for distribution
		uint256 projectsTotalReceivedRents; // Total rents received across all projects
		mapping(address => ProjectDistributionData) projectDets; // Project-specific distribution data
		mapping(address => address) projectSftToProjectAddress; // Mapping of project SFT to project addresses
		uint256 lastFundsDispatchTimestamp; // Timestamp of the last fund distribution
		uint256 shtTotalStakeWeight; // Total weight of all staked SHT tokens
		uint256 shtRewardPerShare; // Reward per share for SHT stakers
		uint256 shtStakingRewards; // Total rewards allocated for SHT staking
		ProjectStakingRewards.Value projectsStakingRewards; // Accumulated staking rewards for projects
		Entities.Value entityFunds; // Funds allocated to entities
	}

	struct ProjectDistributionData {
		uint256 maxShares; // Maximum supply of shares for the project
		uint256 receivedRents; // Total rents received for the project
	}

	/// @notice Sets the total funds for distribution and the genesis epoch. Can only be called once.
	/// @param self The storage struct to set the total funds and genesis epoch.
	/// @param amount The amount of total funds to set.
	function setTotalFunds(Storage storage self, uint256 amount) internal {
		require(self.totalFunds == 0, "Total funds already set");
		self.totalFunds = amount;
		self.lastFundsDispatchTimestamp = block.timestamp;
	}

	/// @notice Returns the total funds available for distribution.
	/// @param self The storage struct containing the total funds.
	/// @return The total funds.
	function getTotalFunds(
		Storage storage self
	) internal view returns (uint256) {
		return self.totalFunds;
	}

	/// @notice Adds rent received for a specific project and updates the total received rents and project-specific data.
	/// @dev Updates the total amount of rent received across all projects and updates the specific project data.
	/// If the `maxShares` for the project has not been set, it retrieves and sets it from the `HousingProject` contract.
	/// @param self The storage struct for the `Distribution` contract where project and rent data is stored.
	/// @param projectAddress The address of the project whose rent is being added.
	/// @param amount The amount of rent received to be added to the project and total received rents.
	function addProjectRent(
		Storage storage self,
		address projectAddress,
		uint256 amount
	) internal {
		self.projectsTotalReceivedRents += amount;

		ProjectDistributionData storage projectData = self.projectDets[
			projectAddress
		];

		// If the maxShares for the project hasn't been set, retrieve and set it from the HousingProject contract.
		if (projectData.maxShares == 0) {
			projectData.maxShares = HousingProject(projectAddress)
				.getMaxSupply();
		}

		projectData.receivedRents += amount;
	}

	/// @notice Adds a new project to the distribution system.
	/// @param self The storage struct for the `Distribution` contract.
	/// @param projectAddress The address of the new project.
	/// @param projectSFTaddress The address of the project's SFT token.
	/// @param maxShares The maximum shares for the project.
	function addProject(
		Storage storage self,
		address projectAddress,
		address projectSFTaddress,
		uint256 maxShares
	) internal {
		self.projectDets[projectAddress].maxShares = maxShares;
		self.projectSftToProjectAddress[projectSFTaddress] = projectAddress;
	}

	/// @notice Computes emissions for a specific epoch based on provided timestamps.
	/// @param epochs The storage struct containing epoch information.
	/// @param epoch The epoch for which to compute emissions.
	/// @param lastTimestamp The timestamp of the last reward generation.
	/// @param latestTimestamp The current timestamp.
	/// @return The computed emissions for the specified time range within the epoch.
	function _computeEdgeEmissions(
		Epochs.Storage memory epochs,
		uint256 epoch,
		uint256 lastTimestamp,
		uint256 latestTimestamp
	) private pure returns (uint256) {
		(uint256 startTimestamp, uint256 endTimestamp) = epochs
			.epochEdgeTimestamps(epoch);

		// Determine the bounds for emission calculation.
		uint256 upperBoundTime;
		uint256 lowerBoundTime;

		// Case 1: The latest timestamp is within the epoch.
		if (
			startTimestamp <= latestTimestamp && latestTimestamp <= endTimestamp
		) {
			upperBoundTime = latestTimestamp;
			lowerBoundTime = startTimestamp;
		}
		// Case 2: The last timestamp is within the epoch.
		else if (
			startTimestamp <= lastTimestamp && lastTimestamp <= endTimestamp
		) {
			upperBoundTime = latestTimestamp <= endTimestamp
				? latestTimestamp
				: endTimestamp;
			lowerBoundTime = lastTimestamp;
		}
		// Case 3: Invalid timestamps.
		else {
			revert("Router._computeEdgeEmissions: Invalid timestamps");
		}

		// Calculate emissions based on the time range.
		return
			Emission.throughTimeRange(
				epoch,
				upperBoundTime - lowerBoundTime,
				epochs.epochLength
			);
	}

	/// @notice Generates rewards for the elapsed epochs since the last funds dispatch.
	/// @dev The rewards are computed per second for precision and distributed across the system.
	/// @param self The storage struct for the `Distribution` contract.
	/// @param epochs The storage struct containing epoch information.
	function generateRewards(
		Storage storage self,
		Epochs.Storage storage epochs
	) internal {
		uint256 currentTimestamp = block.timestamp;
		uint256 lastTimestamp = self.lastFundsDispatchTimestamp;

		// Return early if no time has passed since the last dispatch.
		if (currentTimestamp <= lastTimestamp) {
			return;
		}

		uint256 lastGenerateEpoch = epochs.computeEpoch(lastTimestamp);
		uint256 toDispatch = _computeEdgeEmissions(
			epochs,
			lastGenerateEpoch,
			lastTimestamp,
			currentTimestamp
		);

		uint256 currentEpoch = epochs.currentEpoch();
		if (currentEpoch > lastGenerateEpoch) {
			uint256 intermediateEpochs = currentEpoch - lastGenerateEpoch - 1;

			// Include rewards for all intermediate epochs.
			if (intermediateEpochs > 1) {
				toDispatch += Emission.throughEpochRange(
					lastGenerateEpoch,
					lastGenerateEpoch + intermediateEpochs
				);
			}

			toDispatch += _computeEdgeEmissions(
				epochs,
				currentEpoch,
				lastTimestamp,
				currentTimestamp
			);
		}

		// Convert the total dispatched value into entity-specific funds.
		Entities.Value memory entitiesValue = Entities.fromTotalValue(
			toDispatch
		);

		// Allocate staking rewards and update entity funds.
		uint256 stakingRewards = entitiesValue.staking;
		entitiesValue.staking = 0;
		self.entityFunds.add(entitiesValue);

		// Calculate and distribute the share for SHT stakers.
		uint256 shtStakersShare = (stakingRewards * 7) / 10;

		uint256 totalShtWeight = self.shtTotalStakeWeight;
		if (totalShtWeight > 0) {
			// Increase reward per share based on the stakers' share.
			uint256 rpsIncrease = (shtStakersShare * DIVISION_SAFETY_CONST) /
				totalShtWeight;
			self.shtRewardPerShare += rpsIncrease;
		}

		self.shtStakingRewards += shtStakersShare;
		self.projectsStakingRewards.add(stakingRewards - shtStakersShare);

		// Update the last funds dispatch timestamp to the current timestamp.
		self.lastFundsDispatchTimestamp = currentTimestamp;
	}

	/// @notice Claims staking rewards for SHT holders based on their stake and updates their rewards balance.
	/// @param self The storage struct for the `Distribution` contract.
	/// @param attr The attributes struct for which rewards are being claimed.
	/// @return The total amount of rewards claimed and the updated attributes struct.
	function claimRewards(
		Storage storage self,
		HstAttributes memory attr
	) internal returns (uint256, HstAttributes memory) {
		uint256 shtClaimed = 0;

		// Retrieve the checkpoint value for project token staking rewards.
		uint256 ptRewardCheckpoint = self.projectsStakingRewards.checkpoint;

		// If there are project staking rewards available, calculate the rewards for each project token.
		if (ptRewardCheckpoint > 0) {
			// Iterate through each project token associated with the user.
			for (uint256 i = 0; i < attr.projectTokens.length; i++) {
				// Compute the reward for the project token based on the user's share and the current checkpoint.
				shtClaimed += computeRewardForPT(
					self,
					attr.projectTokens[i],
					attr.projectsShareCheckpoint,
					ptRewardCheckpoint
				);
			}

			// Ensure that the claimed rewards do not exceed the available staking rewards for the projects.
			if (self.projectsStakingRewards.toShare < shtClaimed) {
				shtClaimed = self.projectsStakingRewards.toShare;
			}

			// Deduct the claimed rewards from the total available project staking rewards.
			self.projectsStakingRewards.toShare -= shtClaimed;
		}

		// Retrieve the current reward per share for SHT stakers.
		uint256 shtRPS = self.shtRewardPerShare;

		// If the current reward per share is greater than the last recorded reward per share for the user, calculate the additional rewards.
		if (shtRPS > 0 && attr.shtRewardPerShare < shtRPS) {
			uint256 shtReward = ((shtRPS - attr.shtRewardPerShare) *
				attr.stakeWeight) / DIVISION_SAFETY_CONST;

			// Ensure that the reward calculated does not exceed the available SHT staking rewards.
			if (self.shtStakingRewards < shtReward) {
				shtClaimed = self.shtStakingRewards;
			}

			// Deduct the claimed SHT rewards from the total available SHT staking rewards.
			self.shtStakingRewards -= shtReward;

			// Add the SHT rewards to the total claimed rewards.
			shtClaimed += shtReward;
		}

		// Update the user's attributes with the latest reward per share and project share checkpoint values.
		attr.shtRewardPerShare = shtRPS;
		attr.projectsShareCheckpoint = ptRewardCheckpoint;

		// Return the total claimed rewards and the updated attributes.
		return (shtClaimed, attr);
	}

	/// @notice Computes the reward for a given PT (Housing Project Token).
	/// @param self The storage struct for the `Distribution` contract.
	/// @param tokenPayment The token payment of the housing project.
	/// @param stakingCheckPoint The previous checkpoint value.
	/// @param tokenCheckPoint The new checkpoint value.
	/// @return reward The computed reward for the given PT.
	function computeRewardForPT(
		Storage storage self,
		TokenPayment memory tokenPayment,
		uint256 stakingCheckPoint,
		uint256 tokenCheckPoint
	) internal view returns (uint256 reward) {
		if (
			stakingCheckPoint >= tokenCheckPoint ||
			self.projectsTotalReceivedRents == 0
		) {
			return 0;
		}

		address projectAddress = self.projectSftToProjectAddress[
			tokenPayment.token
		];
		require(
			projectAddress != address(0),
			"Project Address for token not set"
		);

		ProjectDistributionData storage projectData = self.projectDets[
			projectAddress
		];
		require(
			tokenPayment.amount <= projectData.maxShares,
			"Project token amount too large"
		);

		uint256 shareIncrease = tokenCheckPoint - (stakingCheckPoint);
		uint256 projectAllocation = (shareIncrease *
			(projectData.receivedRents)) / (self.projectsTotalReceivedRents);

		reward =
			(projectAllocation * (tokenPayment.amount)) /
			(projectData.maxShares);
	}

	/// @notice Enters staking for the given attributes.
	/// @param self The storage struct for the `Distribution` contract.
	/// @param stakeWeight The stake weight to be added.
	function enterStaking(Storage storage self, uint256 stakeWeight) internal {
		self.shtTotalStakeWeight += (stakeWeight);
	}
}
